00587 bool XWINDOWSDRIVEIMAGEMANAGER::CheckDriveType(XPATH& path, XQWORD& ID)
00588 {
00589   bool                              status = false;
00590 
00591   #ifndef BUILDER
00592 
00593   HANDLE                            hdevice;
00594   PSTORAGE_DEVICE_DESCRIPTOR        pdevdesc;
00595   XWINDOWSDRIVEIMAGE_DEVICE_NUMBER  deviceinfo;
00596   XPATH                             path_noslash;
00597   int                               drivetype;
00598   DWORD                             nbytesreturned;
00599 
00600 
00601   path_noslash = path.Get();
00602 
00603   path_noslash.Slash_Delete();
00604 
00605     // some calls require no tailing slash, some require a trailing slash...
00606 
00607   drivetype = GetDriveType(path.Get());
00608   switch(drivetype)
00609     {
00610       case DRIVE_REMOVABLE  : // The media can be removed from the drive.
00611 
00612       case DRIVE_FIXED      : { // The media cannot be removed from the drive. Some USB drives report as this.
00613                                 hdevice = CreateFile(path_noslash.Get(), FILE_READ_ATTRIBUTES, FILE_SHARE_READ | FILE_SHARE_WRITE, NULL, OPEN_EXISTING, 0, NULL);
00614                                 if(hdevice == INVALID_HANDLE_VALUE) break;
00615 
00616                                 int sizearray  = sizeof(STORAGE_DEVICE_DESCRIPTOR) + 512 - 1;
00617                                 pdevdesc       = (PSTORAGE_DEVICE_DESCRIPTOR)new BYTE[sizearray];
00618 
00619                                 pdevdesc->Size = sizearray;
00620 
00621                                 // get the device number if the drive is removable or (fixed AND on the usb bus, SD, or MMC (undefined in XP/mingw))
00622 
00623                                 if(GetMediaType(hdevice))
00624                                   {
00625                                     status = true;
00626 
00627                                     STORAGE_PROPERTY_QUERY  query;        // input param for query
00628                                     DWORD                   noutbytes;    // IOCTL output length
00629                                     BOOL                    result;       // IOCTL return val
00630                                     DWORD                   nreturned;
00631 
00632                                     // specify the query type
00633                                     query.PropertyId = StorageDeviceProperty;
00634                                     query.QueryType  = PropertyStandardQuery;
00635 
00636                                     // query using IOCTL_STORAGE_QUERY_PROPERTY
00637                                     result = ::DeviceIoControl(hdevice, IOCTL_STORAGE_QUERY_PROPERTY, &query, sizeof(STORAGE_PROPERTY_QUERY), pdevdesc, pdevdesc->Size, &noutbytes, (LPOVERLAPPED)NULL);
00638                                     if(result)
00639                                       {
00640                                         result = ::DeviceIoControl(hdevice, IOCTL_STORAGE_GET_DEVICE_NUMBER, NULL, 0, &deviceinfo, sizeof(XWINDOWSDRIVEIMAGE_DEVICE_NUMBER), &noutbytes, (LPOVERLAPPED)NULL);
00641                                         if(!result)
00642                                           {
00643                                             status = false;
00644 
00645                                           } else status = true;
00646                                       }
00647                                      else
00648                                       {
00649                                         if(DeviceIoControl(hdevice, IOCTL_STORAGE_CHECK_VERIFY2, NULL, 0, NULL, 0, &nreturned, (LPOVERLAPPED) NULL))
00650                                           {
00651 
00652                                           }
00653 
00654                                         status = false;
00655                                       }
00656 
00657                                     if(status)
00658                                       {
00659                                         status = false;
00660 
00661                                         if(((drivetype == DRIVE_REMOVABLE) && (pdevdesc->BusType != BusTypeSata))  ||
00662                                            ((drivetype == DRIVE_FIXED)     && ((pdevdesc->BusType == BusTypeUsb)   || (pdevdesc->BusType == BusTypeSd ) || (pdevdesc->BusType == BusTypeMmc ))))
00663                                           {
00664                                             // ensure that the drive is actually accessible  multi-card hubs were reporting "removable" even when empty
00665 
00666                                             if(DeviceIoControl(hdevice, IOCTL_STORAGE_CHECK_VERIFY2, NULL, 0, NULL, 0, &nbytesreturned, (LPOVERLAPPED) NULL))
00667                                               {
00668                                                 ID = deviceinfo.devicenumber;
00669                                                 status = true;
00670                                               }
00671                                              else
00672                                               {
00673                                                 // IOCTL_STORAGE_CHECK_VERIFY2 fails on some devices under XP/Vista, try the other (slower) method, just in case.
00674 
00675                                                 CloseHandle(hdevice);
00676                                                 hdevice = CreateFile(path_noslash.Get(), FILE_READ_DATA, FILE_SHARE_READ | FILE_SHARE_WRITE, NULL, OPEN_EXISTING, 0, NULL);
00677                                                 if(DeviceIoControl(hdevice, IOCTL_STORAGE_CHECK_VERIFY, NULL, 0, NULL, 0, &nbytesreturned, (LPOVERLAPPED) NULL))
00678                                                   {
00679                                                     ID = deviceinfo.devicenumber;
00680                                                     status = true;
00681                                                   }
00682                                               }
00683                                           }
00684                                       }
00685                                   }
00686 
00687                                 delete[] pdevdesc;
00688 
00689                                 CloseHandle(hdevice);
00690                               }
00691 
00692                               break;
00693 
00694              default       :  break;
00695 
00696     }
00697 
00698   #endif
00699 
00700   return status;
