00715 bool XWINDOWSDRIVEIMAGEMANAGER::GetMediaType(HANDLE hdevice)
00716 {
00717   DISK_GEOMETRY diskgeometry;
00718   DWORD         nbytesreturned;
00719 
00720   if(DeviceIoControl(hdevice, IOCTL_DISK_GET_DRIVE_GEOMETRY,NULL, 0, &diskgeometry, sizeof(diskgeometry), &nbytesreturned, NULL))
00721     {
00722       if((diskgeometry.MediaType == FixedMedia) || (diskgeometry.MediaType == RemovableMedia)) return true; // Not a floppy
00723     }
00724 
00725   return false;
00726 }
