00748   bool          status;
00749 
00750   li.QuadPart = startsector * sectorsize;
00751 
00752   SetFilePointer(handle, li.LowPart, &li.HighPart, FILE_BEGIN);
00753 
00754   status = ReadFile(handle, buffer, (DWORD)size, &bytesread, NULL)?true:false;
00755   if(status)
