00527 bool XWINDOWSFILE::ActualizeSize()
00528 {
00529   if(!isopen)       return false;
00530   if(!filehandle)   return false;
00531 
00532   if(!isreadonly)
00533     {
00534       if(!Flush()) return false;
00535     }
00536 
00537   LARGE_INTEGER exsize;
00538   if(GetFileSizeEx(filehandle, &exsize) == INVALID_FILE_SIZE) return false;
00539 
00540   cachesize  = exsize.HighPart;
00541   cachesize <<=32;
00542   cachesize |= exsize.LowPart;
00543 
00544   return true;
00545 }
