00232       
00233       memset(&saattr, 0, sizeof(saattr));
00234       saattr.nLength              = sizeof(SECURITY_ATTRIBUTES);
00235       saattr.bInheritHandle       = TRUE;
00236       saattr.lpSecurityDescriptor = NULL;
00237 
00238       // Create a pipe for the child process's STDOUT.
00239       if(!CreatePipe(&stdhandle_out_read, &stdhandle_out_write, &saattr, 0))  return false;
00240       // Ensure the read handle to the pipe for STDOUT is not inherited.
00241       if(!SetHandleInformation(stdhandle_out_read, HANDLE_FLAG_INHERIT, 0))   return false;
00242 
00243       // Create a pipe for the child process's STDIN.
00244       if(!CreatePipe(&stdhandle_in_read, &stdhandle_in_write, &saattr, 0))    return false;
00245       // Ensure the read handle to the pipe for STDIN is not inherited.
00246       if(!SetHandleInformation(stdhandle_in_write, HANDLE_FLAG_INHERIT, 0))   return false;
00247 
00248       ZeroMemory(&processinfo, sizeof(PROCESS_INFORMATION));
00249 
00250       ZeroMemory(&startupinfo, sizeof(STARTUPINFO));
00251       startupinfo.cb          = sizeof(startupinfo);
00252       startupinfo.hStdError   = stdhandle_out_write;
00253       startupinfo.hStdOutput  = stdhandle_out_write;
00254       startupinfo.hStdInput   = stdhandle_in_read;    // GetStdHandle(STD_INPUT_HANDLE);
00255       startupinfo.dwFlags    |= STARTF_USESTDHANDLES;
00256 
00257       if(CreateProcessA(NULL, cmdline, NULL, NULL, TRUE, CREATE_NO_WINDOW, NULL, NULL, &startupinfo, &processinfo))
00258         {
00259           CloseHandle(stdhandle_out_write);
00260           
00261           if(in)
00262             {
00263               if(in->GetSize())
00264                 {
00265                   DWORD bytes_write;
00266 
00267                   XBUFFER charstr;
00268                   
00269                   (*in).ConvertToASCII(charstr); 
00270                   WriteFile(stdhandle_in_write, charstr.Get(), in->GetSize(), &bytes_write, NULL);
00271                   
00272                   FlushFileBuffers(stdhandle_in_write);
00273                 }
00274             }
00275 
00276           CloseHandle(stdhandle_in_write);
00277 
00278           if(out)
00279             {
00280               strcpy(outbuf, "");
00281 
00282               for(;;)
00283                 {
00284                   if(!ReadFile(stdhandle_out_read, tempbuf, TEMPBUF_SIZE-1, &bytes_read, NULL))
00285                     {
00286                       break;
00287                     }
00288 
00289                   if(bytes_read > 0)
00290                     {
00291                       tempbuf[bytes_read] = '\0';
00292                       strcat(outbuf, tempbuf);
00293                     }
00294                 }
00295 
00296               (*out) = outbuf;
00297             }
00298 
00299           if(WaitForSingleObject(processinfo.hProcess, (out?INFINITE:500)) == WAIT_OBJECT_0)
00300             {
00301               if(out)
00302                 {
00303                   if(GetExitCodeProcess(processinfo.hProcess, &exitcode))
00304                     {
00305                       if(returncode) (*returncode) = exitcode;
00306                     }
00307 
00308                   CloseHandle(processinfo.hProcess);
00309                   CloseHandle(processinfo.hThread);
00310                 }
00311 
00312               status = true;
00313             }
00314         }
00315     }
00316 
00317   if(cmdline) delete [] cmdline;
00318   if(outbuf)  delete [] outbuf;
00319   if(tempbuf) delete [] tempbuf;
00320 
00321   return status;
00322 }
