00607   if(!sizetitlewindow)
00608     { 
00609       return false;
00610     }
00611                   
00612   title.AdjustSize(sizetitlewindow+1);
00613   int size = GetWindowText(hwnd, title.Get(), sizetitlewindow + 1);
00614   title.AdjustSize();
00615 
00616   if(!size) 
00617     {
00618       title.Empty(); 
00619       return false;
00620     }
00621 
00622   RECT wrect;
00623 
00624   if(GetWindowRect(hwnd, &wrect))
00625     {
00626       rect.x1 = wrect.left;
00627       rect.y1 = wrect.top;
00628       rect.x2 = wrect.right;
00629       rect.y2 = wrect.bottom;
00630     }
00631   
00632   return true;
00633 }
