00647 int XWINDOWSPROCESSMANAGER::GetWindowTitleHeight(HWND hwnd) 
00648 {
00649   RECT window_rectangle; 
00650   RECT client_rectangle;
00651   int  height;
00652   int  width;
00653     
00654   GetWindowRect(hwnd, &window_rectangle);
00655   GetClientRect(hwnd, &client_rectangle);
00656 
00657   height = (window_rectangle.bottom - window_rectangle.top) - (client_rectangle.bottom - client_rectangle.top);
00658   width  = (window_rectangle.right - window_rectangle.left) - (client_rectangle.right - client_rectangle.left);
00659     
00660   return height - (width/2);
00661 }
