00698 bool XWINDOWSPROCESSMANAGER::GetChildProcesses(XVECTOR<XPROCESS*>& applist) 
00699 {
00700   HANDLE threadSnapshot = CreateToolhelp32Snapshot(TH32CS_SNAPTHREAD, 0);
00701   if(threadSnapshot == INVALID_HANDLE_VALUE) 
00702     {
00703       return false;
00704     }
00705 
00706   THREADENTRY32 threadEntry;
00707   threadEntry.dwSize = sizeof(THREADENTRY32);
00708 
00709   if(!Thread32First(threadSnapshot, &threadEntry)) 
00710     {
00711       CloseHandle(threadSnapshot);
00712       return false;
00713     }
00714 
00715   do { XPROCESS* mainprocess = Application_GetProcessByID(threadEntry.th32OwnerProcessID, applist);
00716        if(mainprocess) 
00717          {
00718            if(mainprocess->GetProcessIDs()->IsEmpty())
00719              {
00720                mainprocess->GetProcessIDs()->Add(threadEntry.th32OwnerProcessID); 
00721              }
00722 
00723            mainprocess->GetProcessIDs()->Add(threadEntry.th32ThreadID);
00724          }
00725 
00726       } while (Thread32Next(threadSnapshot, &threadEntry));
00727 
00728   CloseHandle(threadSnapshot);
00729 
00730   return true;
00731 }
