00550 bool XWINDOWSREGISTRYMANAGER::CreateKey(HKEY handlekey, XCHAR* subkeystring, XWINDOWSREGISTRYKEY& registrykey)
00551 {
00552   if(registrykey.GetHandle() != NULL) return false;
00553 
00554   HKEY              oper_handlekey;
00555   DWORD             disposition;
00556   XPATH             subkeystr1;
00557   XSTRING           subkeystr2;
00558   XVECTOR<XSTRING*> partkey;
00559   XSTRING           subkeystr3;
00560   XDWORD            error;
00561 
00562   subkeystr1  = subkeystring;
00563   subkeystr1.Slash_Normalize(true);
00564   subkeystr2 = subkeystr1;
00565 
00566   subkeystr2.Split(__C('\\'), partkey);
00567 
00568   for(XDWORD c=0; c<partkey.GetSize(); c++)
00569      {
00570         subkeystr3 += partkey.Get(c)->Get();
00571 
00572         error = RegCreateKeyEx(handlekey, subkeystr3.Get(), 0, NULL, 0, KEY_ALL_ACCESS | KEY_WOW64_64KEY, NULL, &oper_handlekey, &disposition);
00573         if(error != ERROR_SUCCESS)
00574           {
00575             RegCloseKey(oper_handlekey);
00576             break;
00577           }
00578          else
00579           {
00580             if((partkey.GetSize()-1) != c)
00581               {
00582                 RegCloseKey(oper_handlekey);
00583               }
00584           }
00585 
00586         subkeystr3 +=__C('\\');
00587      }
00588 
00589   if(error == ERROR_SUCCESS)
00590     {
00591       registrykey.SetHandle(oper_handlekey);
00592     }
00593 
00594   partkey.DeleteContents();
00595   partkey.DeleteAll();
00596 
00597   return (error == ERROR_SUCCESS)?true:false;
00598 }
