00752 bool XWINDOWSREGISTRYMANAGER::DeleteKey(HKEY handlekey, XCHAR* subkeystring)
00753 {
00754   HKEY      operhandlekey = NULL;
00755   XSTRING   namekey;
00756   DWORD     size;
00757   LONG      result;
00758   FILETIME  ftwrite;
00759 
00760   result = RegDeleteKey(handlekey, subkeystring);
00761   if(result == ERROR_SUCCESS)  return false;
00762 
00763   result = RegOpenKeyEx(handlekey, subkeystring, 0, KEY_READ, &operhandlekey);
00764   if(result != ERROR_SUCCESS)
00765     {
00766       if(result == ERROR_FILE_NOT_FOUND)
00767              return true;
00768         else return false;
00769     }
00770 
00771   XPATH subkey;
00772 
00773   subkey = subkeystring;
00774   subkey.Slash_Add();
00775 
00776   size = _MAXPATH;
00777 
00778   namekey.AdjustSize(size);
00779   result = RegEnumKeyEx(operhandlekey, 0, namekey.Get(), &size, NULL, NULL, NULL, &ftwrite);
00780   namekey.AdjustSize();
00781 
00782   if(result == ERROR_SUCCESS)
00783     {
00784       do{ subkey.Add(namekey);
00785           if(!DeleteKey(handlekey, namekey))  break;
00786 
00787           size = _MAXPATH;
00788 
00789           namekey.AdjustSize(size);
00790           result = RegEnumKeyEx(operhandlekey, 0, namekey.Get(), &size, NULL, NULL, NULL, &ftwrite);
00791           namekey.AdjustSize();
00792 
00793         } while(result == ERROR_SUCCESS);
00794     }
00795 
00796   RegCloseKey(operhandlekey);
00797 
00798   // Try again to delete the key.
00799   result = RegDeleteKey(handlekey, subkeystring);
00800 
00801  if(result == ERROR_SUCCESS)  return true;
00802 
00803   return false;
00804 }
