00305                   //wprintf(L"\n%s failed to stop.\n", servicename);
00306                 }
00307             }
00308 
00309 
00310           if(DeleteService(service))
00311             {
00312                status = true;
00313             }
00314         }
00315     }
00316 
00317 
00318   if(scmanager)
00319     {
00320       CloseServiceHandle(scmanager);
00321       scmanager = NULL;
00322     }
00323 
