00624   ReportEvent(heventsource, type    ,  // Event type
00625                             0       ,  // Event category
00626                             0       ,  // Event identifier
00627                             NULL    ,  // No security identifier
00628                             2       ,  // Size of lpszStrings array
00629                             0       ,  // No binary data
00630                             strings ,  // Array of strings
00631                             NULL);     // No binary data
00632 
00633 
00634   DeregisterEventSource(heventsource);
