00669 bool XWINDOWSSERVICEBASE::GetServiceConfig(XCHAR* servicename)
00670 {
00671   SC_HANDLE scmanager = OpenSCManager(0, 0, SC_MANAGER_ALL_ACCESS);
00672   if(scmanager == NULL) return false;
00673 
00674   SC_HANDLE service = OpenService(scmanager, servicename, SERVICE_ALL_ACCESS);
00675   if(service == NULL) return false;
00676 
00677   DWORD                   sizeneeded;
00678   DWORD                   buffersize;
00679   DWORD                   error; 
00680   bool                    status = false;
00681 
00682   if(service_config)  LocalFree(service_config);
00683 
00684   if(!QueryServiceConfig(service, NULL, 0, &sizeneeded))
00685     {
00686       error = GetLastError();
00687       if(error == ERROR_INSUFFICIENT_BUFFER)
00688         {
00689           buffersize = sizeneeded;
00690           service_config = (LPQUERY_SERVICE_CONFIG) LocalAlloc(LMEM_FIXED, buffersize);
00691 
00692           status = true;
00693         }       
00694     }
00695 
00696   if(status)
00697     {
00698       status = false;
00699 
00700       if(QueryServiceConfig(service, service_config, buffersize, &sizeneeded))      
00701         {        
00702           status = true;      
00703         }
00704     }
00705 
00706   CloseServiceHandle(scmanager);
00707 
00708   return status;
00709 }
