00932 void WINAPI XWINDOWSSERVICEBASE::ServiceMain(DWORD argc, LPWSTR* argv)
00933 {
00934   assert(service != NULL);
00935 
00936   service->statushandle = RegisterServiceCtrlHandlerEx(service->servicename, (LPHANDLER_FUNCTION_EX)ServiceCtrlHandler, (void*)XWINDOWSSERVICEBASE::service);
00937 
00938   if(service->statushandle == NULL)
00939     {
00940       throw GetLastError();
00941     }
00942 
00943   service->Start(argc, argv);
00944 }
