00956 void WINAPI XWINDOWSSERVICEBASE::ServiceCtrlHandler(DWORD control, DWORD eventtype, LPVOID eventdata, LPVOID context)
00957 {
00958   switch(control)
00959     {
00960       case SERVICE_CONTROL_STOP           : service->Stop();                     break;
00961       case SERVICE_CONTROL_PAUSE          : service->Pause();                    break;
00962       case SERVICE_CONTROL_CONTINUE       : service->Continue();                 break;
00963       case SERVICE_CONTROL_SHUTDOWN       : service->Shutdown();                 break;
00964       case SERVICE_CONTROL_POWEREVENT     : service->PowerEvent(eventtype);      break;
00965       case SERVICE_CONTROL_SESSIONCHANGE  : service->SessionChange(eventtype);   break;
00966       case SERVICE_CONTROL_INTERROGATE    : break;
00967                           default         : break;
00968     }
00969 }
