00456 int XWINDOWSSYSTEM::GetCPUUsageForProcessName(XCHAR* processname)
00457 {
00458   int                      cpuusage = 0;
00459 
00460   #ifndef BUILDER
00461   HANDLE           hprocesssnap;
00462   PROCESSENTRY32   pe32;  
00463 
00464   // Take a snapshot of all processes in the system.
00465   hprocesssnap = CreateToolhelp32Snapshot(TH32CS_SNAPPROCESS, 0);
00466   if(hprocesssnap == INVALID_HANDLE_VALUE )  return false;
00467 
00468   // Set the size of the structure before using it.
00469   memset(&pe32, 0, sizeof(PROCESSENTRY32));
00470   pe32.dwSize = sizeof(PROCESSENTRY32);
00471 
00472   // Retrieve information about the first process, and exit if unsuccessful
00473   if(!Process32First(hprocesssnap, &pe32))
00474     {
00475       CloseHandle(hprocesssnap);  // clean the snapshot object
00476       return false;
00477     }
00478 
00479   // Now walk the snapshot of processes
00480   do{ XSTRING  nametask(pe32.szExeFile);
00481 
00482       //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("Process %s  : [%s]"), processname, nametask.Get());
00483 
00484       if(!nametask.Compare(processname, true))
00485         {
00486            cpuusage = GetCPUUsageForProcessID((XDWORD)pe32.th32ProcessID);
00487         }
00488 
00489     } while(Process32Next(hprocesssnap, &pe32));
00490 
00491   CloseHandle(hprocesssnap);
00492 
00493   #endif
00494 
00495   return cpuusage;
00496 }
