00590 bool XWINDOWSSYSTEM::SetEnviromentVariable(XCHAR* variablename, XCHAR* value)
00591 {
00592   XSTRING                 all;
00593   XWINDOWSREGISTRYMANAGER registrymanager;
00594   XWINDOWSREGISTRYKEY     registrykeyenv;
00595 
00596   for(int c=0; c<2; c++)
00597     {
00598       bool status = false;
00599 
00600       switch(c)
00601         {
00602           case 0: status = registrymanager.OpenKey(HKEY_CURRENT_USER, __L("Environment"), registrykeyenv);
00603                   break;
00604 
00605           case 1: status = registrymanager.OpenKey(HKEY_LOCAL_MACHINE, __L("SYSTEM\\CurrentControlSet\\Control\\Session Manager\\Environment"), registrykeyenv);
00606                   break;
00607         }
00608 
00609       if(status)
00610         {
00611           XVARIANT valuevariant;
00612 
00613           registrykeyenv.ReadValue(variablename, valuevariant);
00614           valuevariant = value;
00615           registrykeyenv.WriteValue(variablename, valuevariant);
00616 
00617           registrymanager.CloseKey(registrykeyenv);
00618         }
00619     }
00620 
00621   all.Format(__L("%s=%s"), variablename, value);
00622 
00623   return _wputenv(all.Get())?false:true;
00624 }
