00682 bool XWINDOWSSYSTEM::GetUserAndDomain(XSTRING& user, XSTRING& domain)
00683 {
00684   HANDLE        htoken   = NULL;
00685   PTOKEN_USER   ptiuser  = NULL;
00686   DWORD         cbti     = 0;
00687   SID_NAME_USE  snu;
00688   bool          status   = false;
00689 
00690   XSTRING       userextend;
00691   
00692   
00693   #ifndef BUILDER
00694    ULONG         size = 1024;
00695    
00696   userextend.AdjustSize(size);  
00697   GetUserNameEx(NameUserPrincipal, userextend.Get(), &size);
00698   userextend.AdjustSize();  
00699   #endif
00700     
00701   if(!OpenThreadToken(GetCurrentThread(), TOKEN_QUERY, TRUE, &htoken)) 
00702     {
00703       if(GetLastError() == ERROR_NO_TOKEN)
00704         {
00705           if(OpenProcessToken(GetCurrentProcess(), TOKEN_QUERY, &htoken))
00706             {
00707               if(!GetTokenInformation(htoken, TokenUser, NULL, 0, &cbti)) 
00708                 {
00709                   ptiuser = (PTOKEN_USER) HeapAlloc(GetProcessHeap(), 0, cbti);
00710                   if(ptiuser) 
00711                     {
00712                       // Retrieve the user information from the token.
00713                       if(GetTokenInformation(htoken, TokenUser, ptiuser, cbti, &cbti))
00714                         {
00715                           TCHAR   _user[1024];
00716                           TCHAR   _domain[1024];
00717                           DWORD   sizeuser      = sizeof(user);
00718                           DWORD   sizedomain    = sizeof(domain);
00719     
00720                           // Retrieve user name and domain name based on user's SID.
00721                           if(LookupAccountSid(NULL, ptiuser->User.Sid, _user, &sizeuser, _domain, &sizedomain, &snu))
00722                             {  
00723                               user.Set(_user);
00724                               domain.Set(_domain);
00725 
00726 
00727                               if(userextend.GetSize()) user = userextend;
00728 
00729                               status = true;
00730                             }
00731                         } 
00732                       
00733                       if(ptiuser)  HeapFree(GetProcessHeap(), 0, ptiuser);                                     
00734                     }             
00735                 }
00736             }
00737         }
00738 
00739       if(htoken) CloseHandle(htoken);
00740     }
00741 
00742   return status;
00743 }
