00757 bool XWINDOWSSYSTEM::ShutDown(XSYSTEM_CHANGESTATUSTYPE type)
00758 {
00759   if(type == XSYSTEM_CHANGESTATUSTYPE_NONE) return false;
00760 
00761   HANDLE            hToken;
00762   TOKEN_PRIVILEGES  tkp;
00763 
00764   // Get a token for this process.
00765   if(!OpenProcessToken(GetCurrentProcess(), TOKEN_ADJUST_PRIVILEGES | TOKEN_QUERY, &hToken))  return false;
00766 
00767   // Get the LUID for the shutdown privilege.
00768   LookupPrivilegeValue(NULL, SE_SHUTDOWN_NAME, &tkp.Privileges[0].Luid);
00769 
00770   tkp.PrivilegeCount            = 1;  // one privilege to set
00771   tkp.Privileges[0].Attributes  = SE_PRIVILEGE_ENABLED;
00772 
00773   // Get the shutdown privilege for this process.
00774   AdjustTokenPrivileges(hToken, FALSE, &tkp, 0, (PTOKEN_PRIVILEGES)NULL, 0);
00775   if(GetLastError() != ERROR_SUCCESS) return false;
00776 
00777   switch(type)
00778     {
00779       case XSYSTEM_CHANGESTATUSTYPE_REBOOT           :  if(!ExitWindowsEx(EWX_REBOOT   | EWX_FORCE, SHTDN_REASON_MAJOR_OPERATINGSYSTEM | SHTDN_REASON_MINOR_UPGRADE | SHTDN_REASON_FLAG_PLANNED)) return false;
00780                                                         break;
00781 
00782       case XSYSTEM_CHANGESTATUSTYPE_SESSION_LOGOFF   :  if(!ExitWindowsEx(EWX_LOGOFF   | EWX_FORCE, SHTDN_REASON_MAJOR_OPERATINGSYSTEM | SHTDN_REASON_MINOR_UPGRADE | SHTDN_REASON_FLAG_PLANNED)) return false;
00783                                                         break;
00784 
00785       case XSYSTEM_CHANGESTATUSTYPE_POWEROFF         :  if(!ExitWindowsEx(EWX_POWEROFF | EWX_FORCE, SHTDN_REASON_MAJOR_OPERATINGSYSTEM | SHTDN_REASON_MINOR_UPGRADE | SHTDN_REASON_FLAG_PLANNED)) return false;
00786                                                         break;
00787 
00788       case XSYSTEM_CHANGESTATUSTYPE_SUSPEND          :    
00789                                                         #ifndef BUILDER
00790                                                         if(!SetSuspendState(TRUE, FALSE, FALSE)) return false; 
00791                                                         #endif    
00792                                                         break;
00793 
00794                                           default    :  return false;
00795 
00796 
00797     }
00798 
00799   return true;
00800 }
