00956   hresult = CoCreateInstance(__uuidof(MMDeviceEnumerator), NULL, CLSCTX_INPROC_SERVER, __uuidof(IMMDeviceEnumerator), (LPVOID *)&deviceenumerator);
00957   if(!deviceenumerator) return false;
00958   if(hresult != S_OK) return false;
00959 
00960   hresult = deviceenumerator->GetDefaultAudioEndpoint(eRender, eConsole, &defaultdevice);
00961   if(hresult != S_OK) return false;
00962  
00963   deviceenumerator->Release();
00964   deviceenumerator = NULL; 
00965 
00966   if(defaultdevice)
00967     {
00968       hresult = defaultdevice->Activate(__uuidof(IAudioEndpointVolume), CLSCTX_INPROC_SERVER, NULL, (LPVOID *)&endpointvolume);
00969       status = (hresult == S_OK)?true:false;
00970 
00971       if(status)
00972         {
00973           defaultdevice->Release();
00974           defaultdevice = NULL;
00975           endpointvolume->GetMasterVolumeLevel(&currentvolume);
00976 
00977           endpointvolume->GetMasterVolumeLevelScalar(&currentvolume);
00978           endpointvolume->SetMute(on, NULL);
00979 
00980           endpointvolume->Release();
00981         }
00982     }
00983 
00984   CoUninitialize();
00985   #endif
00986 
00987   return status;
00988 }
