01003 bool XWINDOWSSYSTEM::GetBatteryLevel(bool& isincharge, XBYTE& levelpercent)
01004 {
01005   SYSTEM_POWER_STATUS powerstatus;
01006 
01007   isincharge    = false;
01008   levelpercent  = 0;
01009 
01010   if(!GetSystemPowerStatus(&powerstatus)) return false;
01011 
01012   if(powerstatus.BatteryLifePercent != 255) 
01013     {
01014       isincharge    = powerstatus.ACLineStatus?true:false;
01015       levelpercent  = powerstatus.BatteryLifePercent;
01016 
01017       return true;
01018     }
01019 
01020   return false;      
01021 }
