01050 
01062 XWINDOWSSYSTEM_CPUUSAGESTATUS* XWINDOWSSYSTEM::GetCPUUsageStatus(XCHAR* processname)
01063 {
01064   if(cpuusagestatus.IsEmpty()) 
01065     {
01066       return NULL;
01067     }
01068 
01069   if(xmutexcheckCPUusage)
01070     {
01071       xmutexcheckCPUusage->Lock();
01072     }
01073 
01074   for(XDWORD c=0; c<cpuusagestatus.GetSize(); c++)
01075     {
01076       XWINDOWSSYSTEM_CPUUSAGESTATUS* cus = cpuusagestatus.Get(c);
01077       if(cus)
01078         {
01079           if(!cus->processname.Compare(processname, true)) 
01080             {
01081               if(xmutexcheckCPUusage)
01082                 {
01083                   xmutexcheckCPUusage->UnLock();
01084                 }
01085 
01086               return cus;
01087             }
01088         }
01089     }
01090 
01091   if(xmutexcheckCPUusage)
01092     {
01093       xmutexcheckCPUusage->UnLock();
01094     }
01095 
01096   return NULL;
01097 }
