01138 XWINDOWSSYSTEM_CPUUSAGESTATUS* XWINDOWSSYSTEM::GetCPUUsageStatus(XDWORD processID)
01139 {
01140   if(cpuusagestatus.IsEmpty()) 
01141     {
01142       return NULL;
01143     }
01144 
01145   if(xmutexcheckCPUusage)
01146     {
01147       xmutexcheckCPUusage->Lock();
01148     }
01149 
01150   for(XDWORD c=0; c<cpuusagestatus.GetSize(); c++)
01151     {
01152       XWINDOWSSYSTEM_CPUUSAGESTATUS* cus = cpuusagestatus.Get(c);
01153       if(cus)
01154         {
01155           if(cus->processID == processID) 
01156             {
01157               if(xmutexcheckCPUusage)
01158                 {
01159                   xmutexcheckCPUusage->UnLock();
01160                 }
01161 
01162               return cus;
01163             }
01164         }    
01165     }
01166 
01167   if(xmutexcheckCPUusage)
01168     {
01169       xmutexcheckCPUusage->UnLock();
01170     }
01171 
01172   return NULL;
01173 }
