00361 bool XWINDOWSVIRTUALDESKTOPMANAGER::IsCurrentDesktop(XCHAR* desktopname)
00362 {
00363   HDESK    hcurrentdesktop    = GetThreadDesktop(GetCurrentThreadId());
00364   XSTRING  currentdesktopname;
00365   DWORD    sizedesktopname    = 0;
00366 
00367   currentdesktopname.AdjustSize(_MAXSTR);
