00502 bool XWINDOWSVIRTUALDESKTOPMANAGER::IsUserInteractive()
00503 {
00504   BOOL isuserinteractive = FALSE;
00505 
00506   HWINSTA hwinstation = GetProcessWindowStation();
00507   if(hwinstation != NULL)
00508     {
00509       USEROBJECTFLAGS uof = { 0 };
00510       if(GetUserObjectInformation(hwinstation, UOI_FLAGS, &uof, sizeof(USEROBJECTFLAGS), NULL) && ((uof.dwFlags & WSF_VISIBLE) == 0))
00511              isuserinteractive = FALSE;
00512         else isuserinteractive = TRUE;
00513     }
00514 
00515   return isuserinteractive?true:false;
