00868                 ULONG             uReturned = 0;
00869 
00870                 // 0 - time out, 1 - One object
00871 
00872                 hr = pEnum->Next(0, 1, &pObj, &uReturned);
00873                 if(FAILED(hr))
00874                   {
00875               XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("Get(IPConnectionMetric). Error code [0x%08X]"), hr);                   
00876                       break;
00877                 }
00878 
00879                 uTotal += uReturned;
00880 
00881                 if(uReturned == 0)  break;
00882 
00883 
00884           // -------------------------------------------------------------------
00885                 // read Index
00886 
00887                 VARIANT vtPropIndex;
00888 
00889                 VariantInit(&vtPropIndex);
00890 
00891                 hr = pObj->Get(L"Index", 0, &vtPropIndex, 0, 0);
00892 
00893                 if(FAILED(hr))
00894                   {
00895               XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("Get(Index). Error code [0x%08X]"), hr);                        
00896                       break;
00897                 }
00898 
00899                 unsigned int index = vtPropIndex.lVal;
00900           
00901                 VariantClear(&vtPropIndex);
00902 
00903 
00904           // -------------------------------------------------------------------
00905                 // read IPConnectionMetric
00906 
00907                 VARIANT vtProp;
00908 
00909                 VariantInit(&vtProp);
00910 
00911                 hr = pObj->Get(L"IPConnectionMetric", 0, &vtProp, 0, 0);
00912 
00913                 if(FAILED(hr))
00914                   {
00915               XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("Get(IPConnectionMetric). Error code [0x%08X]"), hr);                   
00916                       break;
00917                   }
00918 
00919                 unsigned int IPConnectionMetric = vtProp.lVal;
00920           
00921                 VariantClear(&vtProp);
00922 
00923 
00924           // -------------------------------------------------------------------
00925                 // read Description
00926 
00927                 VARIANT vtPropDescription;
00928 
00929                 VariantInit(&vtPropDescription);
00930 
00931                 hr = pObj->Get(L"Description", 0, &vtPropDescription, 0, 0);
00932 
00933                 if(FAILED(hr))
00934                   {
00935               XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("Get(Description). Error code [0x%08X]"), hr);                      
00936                       break;
00937                   }
00938 
00939           XSTRING description_str;
00940 
00941           description_str = vtPropDescription.bstrVal;
00942 
00943                 VariantClear(&vtProp);
00944 
00945          
00946 
00947           XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("Index [%2d] Metric [%2d] [%s] "), index, IPConnectionMetric, description_str.Get());             
00948 
00949 
00950           // -------------------------------------------------------------------
00951                 // Execute SetIPConnectionMetric 
00952 
00953                 IWbemClassObject* pClass    = NULL;
00954                 IWbemClassObject* pOutInst  = NULL;
00955                 IWbemClassObject* pInClass  = NULL;
00956                 IWbemClassObject* pInInst   = NULL;
00957 
00958                 // Get the class object
00959 
00960           BSTR Win32_NetWorkAdapterConfigurationStr = SysAllocString(L"Win32_NetWorkAdapterConfiguration");
00961 
00962                 hr = pSvc->GetObject(Win32_NetWorkAdapterConfigurationStr, 0, NULL, &pClass, NULL);
00963                 if(hr != WBEM_S_NO_ERROR)
00964                   {
00965               XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("getting object Win32_NetWorkAdapterConfiguration. Error code [0x%08X]"), hr);                          
00966                       break;
00967                   }
00968 
00969 
00970           // Get the input argument and set the property
00971 
00972                 hr = pClass->GetMethod(L"SetIPConnectionMetric", 0, &pInClass, NULL); 
00973 
00974                 if(hr != WBEM_S_NO_ERROR)
00975                   {                   
00976               XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("getting method SetIPConnectionMetric. Error code [0x%08X]"), hr);    
00977                       break;
00978                   }
00979 
00980                 hr = pInClass->SpawnInstance(0, &pInInst);
00981                 if(hr != WBEM_S_NO_ERROR)
00982                   {
00983               XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("spawning instance. Error code [0x%08X]"), hr);                         
00984                       break;
00985                   }
00986 
00987 
00988   
00989                 // Set up the query string with the adapter index we obtained above.
00990 
00991                 wchar_t dest[10];
00992 
00993                 memset((char*)dest, 0, 20);
00994                 _itow(index, dest, 10);
00995 
00996                 wstring objPath = L"Win32_NetWorkAdapterConfiguration.Index='" + wstring(dest);
00997 
00998                 objPath = objPath + L"'";
00999 
01000                 BSTR bstrObjPath = SysAllocString(objPath.c_str());
01001 
01002               VARIANT varArg1;
01003 
01004                 VariantInit(&varArg1);
01005 
01006                 //V_VT(&varArg1)    = VT_BSTR;
01007                 //V_BSTR(&varArg1)  = SysAllocString(L"2");
01008 
01009           varArg1.vt    = VT_I4;
01010           varArg1.iVal = metric;
01011 
01012           
01013                 hr = pInInst->Put(L"IPConnectionMetric", 0, &varArg1, CIM_UINT32);
01014                 if(FAILED(hr))
01015                   {
01016               XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("Put(IPConnectionMetric). Error code [0x%08X]"), hr);                               
01017                     break;
01018                   }
01019           BSTR SetIPConnectionMetricStr = SysAllocString(L"SetIPConnectionMetric");
01020                 hr = pSvc->ExecMethod(bstrObjPath, SetIPConnectionMetricStr, 0, NULL, pInInst, &pOutInst, NULL);
01021                 if(hr != WBEM_S_NO_ERROR)
01022                   {
01023               XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("executing SetIPConnectionMetric. Error code [0x%08X]"), hr);                                                   
01024                       break;
01025                   }
01026     
01027 
01028                 // Get the EnableStatic method return value 
01029 
01030                 VARIANT ret_value;
01031 
01032                 hr = pOutInst->Get(L"ReturnValue", 0, &ret_value, 0, 0);
01033                 if(FAILED(hr))
01034                   {
01035               XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("Get(ReturnValue). Error code [0x%08X]"), hr);                                  
01036                       break;
01037                   }
01038 
01039                 long ret = V_I4(&ret_value);
01040                 if(ret != 0)
01041                   {
01042                       hr = E_FAIL;           
01043                   }
01044 
01045                 // Free up BSTR and VARIANT objects
01046 
01047                 VariantClear(&ret_value);
01048               VariantClear(&varArg1);
01049 
01050                 pObj->Release();    // Release objects not owned.            
01051             }
01052       
01053       XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("Number of objects returned is %d"), uTotal);    
01054       }
01055    else
01056       {
01057       XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("querying WMI for network adapters. Error code [0x%08X]"), hr);         
01058       }
01059 
01060   XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("Done"));
01061 
01062   pSvc->Release();
01063   pLoc->Release();     
01064      
01065     return 0;   // Program successfully completed.
01066 }
