00120 bool APPBASE::Ini(XVECTOR<XSTRING*>* execparams)
00121 {
00122   this->execparams  = execparams;
00123 
00124   GEN_XFACTORY_CREATE(xtimerglobal, CreateTimer())
00125   if(!xtimerglobal) return false;
00126     
00127   xtimerglobal->Reset();
00128 
00129   firstupdate = false;
00130 
00131   XDWORD code = GEN_XSYSTEM.GetLanguageSO();
00132 
00133   if(code != XLANGUAGE_ISO_639_3_CODE_INVALID) XTRANSLATION_GEN::GetInstance().Sentences_AddToTranslation(code);
