00422       case APPBASE_EXITTYPE_BY_UPDATE           : exittypestring = XT_L(XTRANSLATION_GEN_ID_APPCONSOLE_EXIT_BY_UPDATE);           break;
00423       case APPBASE_EXITTYPE_BY_APPLICATION      : exittypestring = XT_L(XTRANSLATION_GEN_ID_APPCONSOLE_EXIT_BY_APPLICATION);      break;
00424       case APPBASE_EXITTYPE_BY_USER             : exittypestring = XT_L(XTRANSLATION_GEN_ID_APPCONSOLE_EXIT_BY_USER);             break;
00425       case APPBASE_EXITTYPE_BY_SHUTDOWN         : exittypestring = XT_L(XTRANSLATION_GEN_ID_APPCONSOLE_EXIT_BY_SHUTDOWN);         break;
00426       case APPBASE_EXITTYPE_BY_INVALIDLICENSE   : exittypestring = XT_L(XTRANSLATION_GEN_ID_APPCONSOLE_EXIT_BY_INVALIDLICENSE);   break;
00427       case APPBASE_EXITTYPE_BY_EXPIREDLICENSE   : exittypestring = XT_L(XTRANSLATION_GEN_ID_APPCONSOLE_EXIT_BY_EXPIREDLICENSE) ;  break;
00428                                       default   : return false;                                                                   
00429     }
