00461 void APPBASE::SetSystemChangeStatus(XSYSTEM_CHANGESTATUSTYPE systemchangestatustype)
00462 {
00463   if(this->systemchangestatustype == systemchangestatustype) 
00464     {
00465       return;
00466     }
00467 
00468   this->systemchangestatustype = systemchangestatustype;
00469 
00470   
00471   XSTRING statusstr;  
00472 
00473   switch(systemchangestatustype)
00474     {
00475       case XSYSTEM_CHANGESTATUSTYPE_NONE                :
00476                                   default               : statusstr = __L("normal");                break;
00477       case XSYSTEM_CHANGESTATUSTYPE_REBOOT              : statusstr = __L("reboot");                break;
00478       case XSYSTEM_CHANGESTATUSTYPE_CONSOLE_CONNECT     : statusstr = __L("console connect");       break;
00479       case XSYSTEM_CHANGESTATUSTYPE_CONSOLE_DISCONNECT  : statusstr = __L("console disconnect");    break;
00480       case XSYSTEM_CHANGESTATUSTYPE_SESSION_LOGON       : statusstr = __L("session logon");         break;
00481       case XSYSTEM_CHANGESTATUSTYPE_SESSION_LOGOFF      : statusstr = __L("session logooff");       break;
00482       case XSYSTEM_CHANGESTATUSTYPE_SESSION_LOCK        : statusstr = __L("session lock");          break;
00483       case XSYSTEM_CHANGESTATUSTYPE_SESSION_UNLOCK      : statusstr = __L("session unlock");        break;
00484       case XSYSTEM_CHANGESTATUSTYPE_POWEROFF            : statusstr = __L("power off");             break;
00485       case XSYSTEM_CHANGESTATUSTYPE_SUSPEND             : statusstr = __L("suspend");               break;
00486     }
00487 
00488   //XTRACE_PRINTCOLOR(XTRACE_COLOR_GREEN, __L("[System] Mode change to [%s]"), statusstr.Get());
00489 
00490   APP_XEVENT  xevent(this, APP_XEVENT_TYPE_CHANGESTATUSTYPE);      
00491   xevent.SetChangeStatusType(systemchangestatustype);
00492   PostEvent(&xevent); 
00493 }
