00559 bool APPCFG::SetAutomaticTraceTargets()
00560 {
00561   bool status = false;
00562 
00563   for(int c=0;c<XTRACE_MAXNTARGETS; c++)
00564     {
00565       XSTRING* debugcfg = GetTraceTarget(c);
00566       if(debugcfg)
00567         {
00568           if(!debugcfg->IsEmpty())
00569             {
00570               int      type;
00571               XSTRING  aim;
00572 
00573               aim.AdjustSize(_MAXSTR);
00574               debugcfg->UnFormat(__L("%d,%s"), &type, aim.Get());
00575               aim.AdjustSize();
00576 
00577               XTRACE_SETTARGET(c, (XTRACE_TYPE)type, aim.Get());
00578 
00579               status = true;
00580             }
00581         }
00582     }
00583 
00584   return status;
00585 }
