00861 bool APPCFG::DNSResolver_GetHost(int index, XSTRING& host, XSTRING& IPresolved)
00862 {
00863   if(index < 0)                       return false;
00864   if(index >= hostsresolved.GetSize()) return false;
00865 
00866   host.AdjustSize(_MAXSTR);
00867   IPresolved.AdjustSize(_MAXSTR);
00868 
00869   hostsresolved.Get(index)->UnFormat(__L("%s,%s"), host.Get(), IPresolved.Get());
00870 
00871   host.AdjustSize();
00872   IPresolved.AdjustSize();
00873 
00874   return (host.GetSize() && IPresolved.GetSize())?true:false;
00875 }
00876 
00877 
00889 XSTRING* APPCFG::DNSResolver_GetDNSserver(int index)
00890 {
00891   if(index < 0)                       return NULL;
00892   if(index >= DNSservers.GetSize())   return NULL;
00893 
00894   if(!DNSservers.GetSize()) return NULL;
00895 
00896   return DNSservers.Get(index);
00897 }
00898 
00899 
00909 bool APPCFG::SetAutomaticDNSResolver()
00910 {
00911   for(XDWORD c=0; c<nhostsresolved; c++)
00912     {
00913       XSTRING  host;
00914       XSTRING  IPresolved;
00915 
00916       if(DNSResolver_GetHost(c, host, IPresolved))
00917         {
00918           GEN_DIODNSRESOLVER.Host_Add(host, IPresolved);
00919         }
00920     }
00921 
00922   for(XDWORD c=0; c<nDNSservers; c++)
00923     {
00924       XSTRING* dnsserver = DNSResolver_GetDNSserver(c);
00925       if(dnsserver)
00926         {
00927           GEN_DIODNSRESOLVER.DNSServer_AddDNSServer(dnsserver->Get());
00928         }
00929     }
00930 
00931   return true;
00932 }
