00357           APPCHECKRESOURCESHARDWARE_XEVENT  xevent(this, APPCHECKRESOURCESHARDWARE_XEVENT_TYPE_TOTALCPUUSAGELIMIT);        
00358           xevent.SetActualTotalCPUUsage(cpuusage);      
