00378 bool APPINTERNETSERVICES::ChangeCadenceCheck(APPINTERNETSERVICES_TASKID taskID, int timecadenceseconds, bool startimmediatelycycles)
00379 {
00380   if(!xscheduler)
00381     {
00382       return false;
00383     } 
