00390   XSCHEDULERTASK* task = NULL; 
00391 
00392   xscheduler->GetMutexScheduler()->Lock();
00393 
00394   task = xscheduler->Task_GetForID(taskID);        
00395   if(task) 
00396     {      
00397       task->SetNCycles(XSCHEDULER_CYCLEFOREVER, timecadenceseconds);
00398       task->SetIsStartImmediatelyCycles(startimmediatelycycles);
00399       task->SetIsActive(true);      
00400 
00401       task->ResetCondition();
00402     }
00403 
00404   xscheduler->GetMutexScheduler()->UnLock();       
00405 
00406   return task?true:false;
