00643 bool APPINTERNETSERVICES::CheckInternetStatus()
00644 {
00645   APPINTERNETSERVICES_XEVENT xevent(this, APPINTERNETSERVICES_XEVENT_TYPE_CHECKINTERNETCONNEXION);      
00646 
00647   xevent.SetInternetConnexionState(APPINTERNETSERVICES_CHECKINTERNETCONNEXION_STATE_CHECKING);  
00648   PostEvent(&xevent);
00649 
00650   haveinternetconnection = checkinternetconnection->Check();
00651 
00652   if(checkinternetconnection->IsChangeConnectionStatus())
00653     {
00654       if(haveinternetconnection)
