00656           DIOCHECKTCPIPCONNECTION_CUT*  connectioncut =  checkinternetconnection->GetLastConnectionsCut();
00657           XSTRING                       measuretime;
00658 
00659           if(connectioncut)
00660             {
00661               connectioncut->GetMeasureTimeString(measuretime, true);
00662               
00663               xevent.SetInternetConnexionState(APPINTERNETSERVICES_CHECKINTERNETCONNEXION_STATE_RESTORE);
00664               xevent.SetInternetConnextionCut(connectioncut);
00665 
00666               if(cfg->InternetServices_GetCheckInternetStatusCadence())
00667                 {
00668                   // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Get Internet Services cadence %d]"), cfg->InternetServices_GetCheckInternetStatusCadence());
00669 
00670                   ChangeCadenceCheck(APPINTERNETSERVICES_TASKID_CHECKCONNECTIONINTERNET, cfg->InternetServices_GetCheckInternetStatusCadence());     
00671                 }
00672 
00673               if(cfg->InternetServices_GetCheckIPsChangeCadence())
00674                 {
00675                   // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Get IPs ajust to 30 seconds]"));
00676 
00677                   ChangeCadenceCheck(APPINTERNETSERVICES_TASKID_GETIPS, 30, false);                        
00678                 }
00679             }
00680            else
00681             {
00682               xevent.SetInternetConnexionState(APPINTERNETSERVICES_CHECKINTERNETCONNEXION_STATE_CHECKED); 
00683             }
00684         }
00685        else
00686         {
00687           xevent.SetInternetConnexionState(APPINTERNETSERVICES_CHECKINTERNETCONNEXION_STATE_CUT);
00688 
00689           if(cfg->InternetServices_GetCheckInternetStatusCadence())
00690             {
00691               // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Get Internet Services cadence 10]"));    
00692 
00693               ChangeCadenceCheck(APPINTERNETSERVICES_TASKID_CHECKCONNECTIONINTERNET, 10, false);                       
00694             }
00695         }              
00696     }
00697    else
00698     {
00699       xevent.SetInternetConnexionState(APPINTERNETSERVICES_CHECKINTERNETCONNEXION_STATE_CHECKED); 
00700     }
