00765       DIOIP ip;
00766       
00767       status = false;
00768 
00769       // Check Local IP changed
00770       if(automaticlocalIP.Compare(actualautomaticlocalIP))
00771         {                                                                      
00772           xevent.SetIsChangeLocalIP(true);          
00773           xevent.GetChangeLocalIP()->Set(actualautomaticlocalIP);
00774 
