00949       case APPINTERNETSERVICES_TASKID_GETIPS                  : // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Check Get IPs..."));    
00950 
00951                                                                 if(CheckInternetConnection()) 
00952                                                                   { 
00953                                                                     XSTRING actualpublicIP;                                                                                                                             
00954 
00955                                                                     if(UpdateIPs(actualpublicIP))
00956                                                                       {  
00957                                                                         // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Update public IP"));    
00958   
00959                                                                         #ifdef XTRACE_ACTIVE                                                                   
00960                                                                         XTRACE_RESOLVEALLRESOURCES; 
00961                                                                         #endif  
00962 
00963                                                                         #ifdef APP_CFG_DYNDNSMANAGER_ACTIVE
00964                                                                         UpdateDynDNSURLs(actualpublicIP);                                                                        
00965                                                                         #endif
00966                                                                       }   
00967 
00968                                                                     if(!actualpublicIP.IsEmpty())
00969                                                                       {
00970                                                                         #ifdef APP_CFG_DYNDNSMANAGER_ACTIVE                                                                  
00971                                                                         // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Get IPs ajust to %d seconds]"), cfg->InternetServices_GetCheckIPsChangeCadence());
00972                                                                         ChangeCadenceCheck(APPINTERNETSERVICES_TASKID_GETIPS, cfg->InternetServices_GetCheckIPsChangeCadence());   
00973                                                                         #endif                                                                                                                                           
00974                                                                       }
00975                                                                   }
00976                                                                 break;
00977 
00978       case APPINTERNETSERVICES_TASKID_CHECKNTPDATETIME        : if(CheckInternetConnection()) 
00979                                                                  {
00980                                                                    AdjustTimerByNTP(&NTPservers);                                                                  
00981                                                                  }
00982                                                                 break;
00983     }
00984 
00985   
00986   if(checkinternetconnection)
00987     {
00988       checkinternetconnection->GetCheckConnections()->Connections_GetMutex()->UnLock();
00989     }
00990 
00991 }
