01006 void APPINTERNETSERVICES::HandleEvent(XEVENT* xevent)
01007 {
01008   if(!xevent) return;
01009 
01010   switch(xevent->GetEventFamily())
01011     {
01012       case XEVENT_TYPE_SCHEDULER       : {  XSCHEDULER_XEVENT* event = (XSCHEDULER_XEVENT*)xevent;
01013                                             if(!event) return;
01014 
01015                                             HandleEvent_Scheduler(event);
01016                                          }
01017                                          break;
01018     }
01019 }
