00437   XDWORD events[] = {  DIOAPPLICATIONUPDATE_XEVENT_TYPE_GETVERSIONAVAILABLE                                    ,
00438                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_CHECKVERSIONAVAILABLE                                    ,
00439                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_DOWNLOADFILE                                           ,
00440                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_UNZIPFILE                                              ,
00441                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_DOWNLOADFILES                                          ,
00442                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_BACKUPORIGINALFILE                                     ,
00443                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_BACKUPORIGINALFILES                                    ,
00444                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_APP_RELEASERESOURCES                                   ,
00445                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEFILE                                             ,
00446                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEEXECFILE                                         ,
00447                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEFILES                                            ,
00448                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_APP_END                                                ,
00449                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_RESTOREUPDATEFILE                                      ,
00450                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_RESTOREUPDATEFILES                                     ,
00451                     };
00452 
00453 
00454   for(XDWORD c=0; c< sizeof(events)/sizeof(XDWORD); c++)
00455     {
00456       switch(eventoperation)
00457         {
00458           case APPUPDATE_EVENTOPERATION_REGISTER     : RegisterEvent(events[c]);                           break;
00459           case APPUPDATE_EVENTOPERATION_DEREGISTER   : DeRegisterEvent(events[c]);                         break;
00460           case APPUPDATE_EVENTOPERATION_SUBSCRIBE    : SubscribeEvent(events[c]   , xsubject, observer);   break;
00461           case APPUPDATE_EVENTOPERATION_UNSUBSCRIBE  : UnSubscribeEvent(events[c] , xsubject, observer);   break;
00462                                            default   : return false;
00463         }
00464     }
00465 
00466   return true;
