00766 bool APPUPDATE::LogOfEvent(DIOAPPLICATIONUPDATE_XEVENT* event)
00767 {
00768   DIOAPPLICATIONUPDATE_FILE*  updatefile = event->GetActualFileToUpdate();
00769   XSTRING                     string;
00770 
00771   switch(event->GetEventType())
00772     {
00773       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_GETVERSIONAVAILABLE       : if(event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR)
00774                                                                           {
00775                                                                             string.Format( __L("Update Version available %d.%d.%d")
00776                                                                                           , event->GetVersionData()->GetVersion()
00777                                                                                           , event->GetVersionData()->GetSubVersion()
00778                                                                                           , event->GetVersionData()->GetSubVersionError());
00779                                                                           }
00780                                                                          else
00781                                                                           {
00782                                                                             string.Format( __L("No version available to update"));
00783                                                                           }
00784                                                                         break;
00785 
00786       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_CHECKVERSIONAVAILABLE     : string.Format( __L("Version %d.%d.%d is ")
00787                                                                                       , event->GetVersionData()->GetVersion()
00788                                                                                       , event->GetVersionData()->GetSubVersion()
00789                                                                                       , event->GetVersionData()->GetSubVersionError());
00790 
00791                                                                         switch(event->GetVersionComparation())
00792                                                                           {
00793                                                                             case -1 : string.Add(__L("old"));   break;
00794                                                                             case  0 : string.Add(__L("equal"));  break;
00795                                                                             case  1 : string.Add(__L("new"));    break;
00796                                                                           }
00797 
00798                                                                         if(event->GetVersionComparation() <=0) string.AddFormat(__L(". It will not be updated"));
00799 
00800                                                                         break;
00801 
00802 
00803       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_DOWNLOADFILE              : if(updatefile) string.Format( __L("Download file %s (%dk).")
00804                                                                                                       , updatefile->GetXPathFile()->Get()
00805                                                                                                       , updatefile->GetSize()/1024);
00806 
00807                                                                         break;
00808 
00809       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_UNZIPFILE                 : if(updatefile) string.Format( __L("Unzip file %s (%dk) -> %s.")
00810                                                                                                       , updatefile->GetXPathFile()->Get()
00811                                                                                                       , updatefile->GetSize()/1024
00812                                                                                                       , event->GetActualUnzipFileName()->Get());
00813                                                                         break;
00814 
00815       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_DOWNLOADFILES             : string.Format(__L("Downloads file %s."), (event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR?__L("Ok."):__L("ERROR!")));
00816                                                                         break;
00817 
00818       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_BACKUPORIGINALFILE        : if(updatefile) string.Format( __L("Backup original file %s (%dk).")
00819                                                                                                       , updatefile->GetXPathFile()->Get()
00820                                                                                                       , updatefile->GetSize()/1024);
00821                                                                         break;
00822 
00823       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_BACKUPORIGINALFILES       : string.Format(__L("Backup Original file %s."), (event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR?__L("Ok."):__L("ERROR!")));
00824                                                                         break;
00825 
00826       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_APP_RELEASERESOURCES      : string.Format(__L("Application release blocked resources"));
00827                                                                         break;
00828 
00829 
00830       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEFILE                : if(updatefile) string.Format( __L("Copy update file %s (%dk).")
00831                                                                                                       , updatefile->GetXPathFile()->Get()
00832                                                                                                       , updatefile->GetSize()/1024);
00833                                                                         break;
00834 
00835        case DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEEXECFILE           : if(updatefile) string.Format( __L("Notify update EXEC file %s (%dk).")
00836                                                                                                       , updatefile->GetXPathFile()->Get()
00837                                                                                                       , updatefile->GetSize()/1024);
00838                                                                         break;
00839 
00840       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEFILES               : string.Format(__L("Update files %s."), (event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR?__L("Ok."):__L("ERROR!")));
00841                                                                         break;
00842 
00843 
00844       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_APP_END                   : string.Format(__L("Terminate application"));
00845                                                                         break;
00846 
00847       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_RESTOREUPDATEFILE         : if(updatefile) string.Format( __L("Restore file %s from backup (%dk).")
00848                                                                                                     , updatefile->GetXPathFile()->Get()
00849                                                                                                     , updatefile->GetSize()/1024);
00850                                                                         break;
00851 
00852       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_RESTOREUPDATEFILES        : string.Format(__L("Restore update files %s."), (event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR?__L("Ok."):__L("ERROR!")));
00853                                                                         break;
00854     }
00855 
00856   if(string.GetSize())
00857     {
00858       XLOG::GetInstance().AddEntry((event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR)?XLOGLEVEL_INFO:XLOGLEVEL_ERROR, DIOAPPLICATIONUPDATE_LOG_SECTIONID_APPUPDATE, false, string.Get());
00859       return true;
00860     }
00861 
00862   return false;
00863 }
