00910 bool APPUPDATE::CreateTaskUpdate(bool doajust)
00911 {
00912   if(!app_cfg) return false;
00913 
00914   XSCHEDULERTASK* xtask = new XSCHEDULERTASK(xscheduler);
00915   if(!xtask) return false;
00916 
00917   bool toactive = false;
00918 
00919   XDATETIME* GEN_XFACTORY_CREATE(start, CreateDateTime())
00920   XDATETIME* GEN_XFACTORY_CREATE(end, CreateDateTime())
00921   if(start && end)
00922     {
00923       if(!app_cfg->ApplicationUpdate_GetCheckTime()->IsEmpty())
00924         {
00925           start->Read();
00926 
00927           start->GetDateTimeFromString((*app_cfg->ApplicationUpdate_GetCheckTime()), XDATETIME_FORMAT_ADDTIME);
00928           start->SetSeconds(0);
00929           start->SetMilliSeconds(0);
00930 
00931           start->AddDays(1);
00932 
00933           end->Set(start);
00934           end->AddSeconds(5);
00935 
00936           xtask->SetTimeLimits(start, end);
00937 
00938           toactive = true;
00939         }
00940        else
00941         {
00942           int minutescadence = app_cfg->ApplicationUpdate_GetCheckCadence();
00943           if(minutescadence)
00944             {
00945               start->Read();
00946 
00947               if(doajust)
00948                 {
00949                   int minutes = ((start->GetMinutes() / minutescadence) + ((start->GetMinutes() % minutescadence)?1:0)) * minutescadence;
00950                   if(minutes == start->GetMinutes()) minutes += minutescadence;
00951                   if(minutes >= 60)
00952                     {
00953                       start->AddHours(1);
00954                       minutes = 0;
00955                     }
00956 
00957                   start->SetMinutes(minutes);
00958 
00959                 } else start->AddMinutes(minutescadence);
00960 
00961               start->SetSeconds(0);
00962               start->SetMilliSeconds(0);
00963 
00964               xtask->SetTimeLimits(start);
00965 
00966               toactive = true;
00967             }
00968         }
00969     }
00970 
00971   if(toactive)
00972     {
00973       xtask->SetID(APPUPDATE_TASKID_CHECKUPDATEAPPLICATION);
00974       xtask->SetNCycles(1);
00975       xtask->SetIsActive(true);
00976 
00977       xscheduler->Task_Add(xtask);
00978     }
00979    else delete xtask;
00980 
00981   GEN_XFACTORY.DeleteDateTime(start);
00982   GEN_XFACTORY.DeleteDateTime(end);
00983 
00984   return true;
00985 }
