01040 void APPUPDATE::HandleEvent(XEVENT* xevent)
01041 {
01042   if(!xevent) return;
01043 
01044   switch(xevent->GetEventFamily())
01045     {
01046       case XEVENT_TYPE_SCHEDULER           : { XSCHEDULER_XEVENT* event = (XSCHEDULER_XEVENT*)xevent;
01047                                               if(!event) return;
01048 
01049                                               HandleEvent_Scheduler(event);
01050                                             }
01051                                             break;
01052 
01053       case XEVENT_TYPE_APPLICATIONUPDATE   : { DIOAPPLICATIONUPDATE_XEVENT* event = (DIOAPPLICATIONUPDATE_XEVENT*)xevent;
01054                                               if(!event) return;
01055 
01056                                               HandleEvent_ApplicationUpdate(event);
01057                                             }
01058                                             break;
01059     }
01060 
01061 }
