00260 bool APPWEBSERVER::SendRequest(DIOWEBSERVER_CONNECTION* connection, DIOWEBHEADER_RESULT headerresult, XBUFFER* data, int timeout, XSTRING* addhead)
00261 {
00262   DIOWEBSERVER_HEADER webserverheader;
00263   XSTRING             entitytag;
00264   HASHMD5             hashmd5;
00265   bool                iscached = false;
00266   bool                status   = false;
00267 
00268   if(!connection)                                return false;
00269   if(!connection->GetDIOStream())                return false;
00270   if(!connection->GetDIOStream()->IsConnected()) return false;
00271 
00272   if(data)
00273     {
00274       hashmd5.Do((*data));
00275       hashmd5.GetResultString(entitytag);
00276       iscached = connection->GetRequest()->GetIfNoneMatch()->Compare(entitytag)?false:true;
00277     }
00278 
00279   if(iscached)
00280     {
00281       webserverheader.Create(connection->GetRequest()->GetResource(), 1, 1, DIOWEBHEADER_RESULT_NOTMODIFIED, connection->GetRequest()->GetSize());
00282       status = webserverheader.Write(connection->GetDIOStream(), 5);
00283     }
00284    else
00285     {
00286       XPATH resourceext;
00287       int   size = (data ?(int)data->GetSize():-1);
00288 
00289       resourceext = connection->GetRequest()->GetResource()->Get();
00290       resourceext.SetOnlyExt();
00291 
00292       webserverheader.Create(connection->GetRequest()->GetResource(), 1, 1, headerresult, size, &entitytag, connection->GetServer()->IsPlayablePage(resourceext));
00293       if(addhead) webserverheader.AddLine(addhead->Get());
00294       status = webserverheader.Write(connection->GetDIOStream(), 5);
00295       if(status)
00296         {
00297           if(size > 0) status = connection->Send((*data), timeout);
00298         }
00299     }
00300 
00301   return status;
