00821 void APPWEBSERVER::HandleEvent_WebServer(DIOWEBSERVER_XEVENT* event)
00822 {
00823   switch(event->GetEventType())
00824     {
00825       case DIOWEBSERVER_XEVENT_TYPE_REQUEST                : ResolveRequest(event->GetServer(), event->GetConnection(), event->GetRequest());
00826                                                              break;
00827 
00828       case DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_CONNECTED    : if(!PostEvent(event))
00829                                                               {
00830                                                                 // Accept connection with any version, protocol o resources.
00831                                                                 event->SetStatus(true);
00832                                                                 break;
00833                                                               }
00834                                                              break;
00835 
00836       case DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_READDATA     : PostEvent(event);
00837                                                              break;
00838 
00839       case DIOWEBSERVER_XEVENT_TYPE_WEBSOCKET_DISCONNECTED : PostEvent(event);
00840                                                              break;
00841     }
00842 }
