00422 void CIPHERCURVE25519::freduce_degree(XQWORDSIG* output) 
00423 {
00424   // Each of these shifts and adds ends up multiplying the value by 19.
00425   output[8] += output[18] << 4;
00426   output[8] += output[18] << 1;
00427   output[8] += output[18];
00428   output[7] += output[17] << 4;
00429   output[7] += output[17] << 1;
00430   output[7] += output[17];
00431   output[6] += output[16] << 4;
00432   output[6] += output[16] << 1;
00433   output[6] += output[16];
00434   output[5] += output[15] << 4;
00435   output[5] += output[15] << 1;
00436   output[5] += output[15];
00437   output[4] += output[14] << 4;
00438   output[4] += output[14] << 1;
00439   output[4] += output[14];
00440   output[3] += output[13] << 4;
00441   output[3] += output[13] << 1;
00442   output[3] += output[13];
00443   output[2] += output[12] << 4;
00444   output[2] += output[12] << 1;
00445   output[2] += output[12];
00446   output[1] += output[11] << 4;
00447   output[1] += output[11] << 1;
00448   output[1] += output[11];
00449   output[0] += output[10] << 4;
00450   output[0] += output[10] << 1;
00451   output[0] += output[10];
00452 }
