00466 XQWORDSIG CIPHERCURVE25519::div_by_2_26(const XQWORDSIG v) 
00467 {  
00468   const XDWORD highword     = (XDWORD)(((XQWORD) v) >> 32);   // High word of v; no shift needed
00469   const XDWORDSIG sign      = ((XDWORDSIG) highword) >> 31;   // Set to all 1s if v was negative; else set to 0s.   
00470   const XDWORDSIG roundoff  = ((XDWORD) sign) >> 6;           // Set to 0x3ffffff if v was negative; else set to 0. 
00471   
00472   return (v + roundoff) >> 26;    // Should return v / (1<<26) 
00473 }
00474 
00475 
00487 XQWORDSIG CIPHERCURVE25519::div_by_2_25(const XQWORDSIG v) 
00488 {  
00489   const XDWORD    highword  = (XDWORD)(((XQWORD) v) >> 32);   // High word of v; no shift needed  
00490   const XDWORDSIG sign      = ((XDWORDSIG) highword) >> 31;   // Set to all 1s if v was negative; else set to 0s.   
00491   const XDWORDSIG roundoff  = ((XDWORD) sign) >> 7;           // Set to 0x1ffffff if v was negative; else set to 0. 
00492  
00493   return (v + roundoff) >> 25; // Should return v / (1<<25) 
00494 }
