00496 
00508 XDWORDSIG CIPHERCURVE25519::div_s32_by_2_25(const XDWORDSIG v) 
00509 {
00510    const XDWORDSIG roundoff = ((XDWORD)(v >> 31)) >> 7;
00511 
00512    return (v + roundoff) >> 25;
00513 }
