00528 void CIPHERCURVE25519::freduce_coefficients(XQWORDSIG* output) 
00529 {
00530   XDWORD i;
00531 
00532   output[10] = 0;
00533 
00534   for(i=0; i<10; i+=2) 
00535     {
00536       XQWORDSIG over = div_by_2_26(output[i]);
00537 
00538       output[i] -= over << 26;
00539       output[i+1] += over;
00540 
00541       over = div_by_2_25(output[i+1]);
00542       output[i+1] -= over << 25;
00543       output[i+2] += over;
00544     }
00545 
00546   // Now |output[10]| < 2 ^ 38 and all other coefficients are reduced. 
00547   output[0] += output[10] << 4;
00548   output[0] += output[10] << 1;
00549   output[0] += output[10];
00550 
00551   output[10] = 0;
00552 
00553   // Now output[1..9] are reduced, and |output[0]| < 2^26 + 19 * 2^38  So |over| will be no more than 77825  
00554   {
00555     XQWORDSIG over = div_by_2_26(output[0]);
00556     output[0] -= over << 26;
00557     output[1] += over;
00558   }
00559 
00560   // Now output[0,2..9] are reduced, and |output[1]| < 2^25 + 77825  So |over| will be no more than 1. 
00561   {
00562     // output[1] fits in 32 bits, so we can use div_s32_by_2_25 here. 
00563     XDWORDSIG over32 = div_s32_by_2_25((XDWORDSIG)output[1]);
00564     output[1] -= over32 << 25;
00565     output[2] += over32;
00566   }
00567 
00568   // Finally, output[0,1,3..9] are reduced, and output[2] is "nearly reduced": 
00569   // we have |output[2]| <= 2^26.  This is good enough for all of our math, but it will require an extra freduce_coefficients before fcontract.
00570 }
