00587 void CIPHERCURVE25519::fmul(XQWORDSIG* output, const XQWORDSIG* in, const XQWORDSIG* in2) 
00588 {
00589   XQWORDSIG t[19];
00590 
00591   fproduct(t, in, in2);
00592   freduce_degree(t);
00593   freduce_coefficients(t);
00594 
00595   memcpy(output, t, sizeof(XQWORDSIG) * 10);
00596 }
